  `description` text COLLATE utf8mb4_unicode_520_ci NOT NULL,
  `code` longtext COLLATE utf8mb4_unicode_520_ci NOT NULL,
  `tags` longtext COLLATE utf8mb4_unicode_520_ci NOT NULL,
  `scope` varchar(15) COLLATE utf8mb4_unicode_520_ci NOT NULL DEFAULT 'global',
  `priority` smallint(6) NOT NULL DEFAULT '10',
  `active` tinyint(1) NOT NULL DEFAULT '0',
  PRIMARY KEY (`id`)
) ENGINE=MyISAM AUTO_INCREMENT=6 DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_520_ci;

--
-- Déchargement des données de la table `wp_pbsnippets`
--

INSERT INTO `wp_pbsnippets` (`id`, `name`, `description`, `code`, `tags`, `scope`, `priority`, `active`) VALUES
(1, 'Example HTML shortcode', 'This is an example snippet for demonstrating how to add an HTML shortcode. You can remove it, or edit it to add your own content.', '\nadd_shortcode( \'shortcode_name\', function () {\n\n	$out = \'<p>write your HTML shortcode content here</p>\';\n\n	return $out;\n} );', 'shortcode', 'global', 10, 0),
(2, 'Example CSS snippet', 'This is an example snippet for demonstrating how to add custom CSS code to your website. You can remove it, or edit it to add your own content.', '\nadd_action( \'wp_head\', function () { ?>\n	<style>\n\n		/* write your CSS code here */\n\n	</style>\n<?php } );\n', 'css', 'front-end', 10, 0),
(3, 'Example JavaScript snippet', 'This is an example snippet for demonstrating how to add custom JavaScript code to your website. You can remove it, or edit it to add your own content.', '\nadd_action( \'wp_head\', function () { ?>\n	<script>\n\n		/* write your JavaScript code here */\n\n	</script>\n<?php } );\n', 'javascript', 'front-end', 10, 0),
(4, 'Order snippets by name', 'Order snippets by name by default in the snippets table. You can remove it, or edit it to add your own content.', '\nadd_filter( \'code_snippets/list_table/default_orderby\', function () {\n	return \'name\';\n} );\n', 'code-snippets-plugin', 'admin', 10, 0),
(5, 'Code Buttons', '', 'function custom_button_shortcode( $atts, $content = null ) {\r\n   \r\n    // shortcode attributes\r\n    extract( shortcode_atts( array(\r\n        \'url\'    => \'\',\r\n        \'title\'  => \'\',\r\n        \'target\' => \'\',\r\n        \'text\'   => \'\',\r\n    ), $atts ) );\r\n \r\n    $content = $text ? $text : $content;\r\n \r\n    // Returns the button with a link\r\n    if ( $url ) {\r\n \r\n        $link_attr = array(\r\n            \'href\'   => esc_url( $url ),\r\n            \'title\'  => esc_attr( $title ),\r\n            \'target\' => ( \'blank\' == $target ) ? \'_blank\' : \'\',\r\n            \'class\'  => \'custombutton\'\r\n        );\r\n \r\n        $link_attrs_str = \'\';\r\n \r\n        foreach ( $link_attr as $key => $val ) {\r\n \r\n            if ( $val ) {\r\n \r\n                $link_attrs_str .= \' \' . $key . \'=\"\' . $val . \'\"\';\r\n \r\n            }\r\n \r\n        }\r\n \r\n \r\n        return \'<a\' . $link_attrs_str . \'><span>\' . do_shortcode( $content ) . \'</span></a>\';\r\n \r\n    }\r\n \r\n    // Return as span when no link defined\r\n    else {\r\n \r\n        return \'<span class=\"custombutton\"><span>\' . do_shortcode( $content ) . \'</span></span>\';\r\n \r\n    }\r\n \r\n}\r\nadd_shortcode( \'custombutton\', \'custom_button_shortcode\' );', '', 'global', 10, 1);

